import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import { CartContext } from '../contexts/CartContext';

/**
 * Reusable card component to display a single product summary.  The
 * layout roughly follows the cards on the original site: a top image
 * area, pricing information with current and old prices, the
 * product name and a simplified star rating.  Clicking the card
 * navigates to the product details page.
 */
function ProductCard({ product }) {
  // Compute discount percentage if both prices are provided
  const discount = product.oldPrice
    ? Math.round(((product.oldPrice - product.price) / product.oldPrice) * 100)
    : null;

  return (
    <div
      className="product-card"
      style={{
        border: '1px solid var(--color-border)',
        borderRadius: 4,
        overflow: 'hidden',
        backgroundColor: '#fff',
        display: 'flex',
        flexDirection: 'column',
      }}
    >
      <Link to={`/product/${product.id}`} style={{ display: 'block', position: 'relative', paddingTop: '66.66%' }}>
        {/* Placeholder image fills the card area using absolute positioning */}
        <div
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            backgroundColor: '#e9e7e3',
          }}
        ></div>
      </Link>
      <div style={{ padding: '0.5rem', flex: '1 1 auto', display: 'flex', flexDirection: 'column' }}>
        {/* Price row */}
        <div style={{ display: 'flex', alignItems: 'baseline', gap: '0.5rem' }}>
          <span style={{ color: 'var(--color-primary)', fontWeight: 600 }}>
            {product.price.toLocaleString('ru-RU')} â‚½
          </span>
          {product.oldPrice && (
            <span style={{ textDecoration: 'line-through', color: 'var(--color-muted)', fontSize: '0.875rem' }}>
              {product.oldPrice.toLocaleString('ru-RU')} â‚½
            </span>
          )}
          {discount && (
            <span style={{ backgroundColor: 'var(--color-primary)', color: '#fff', fontSize: '0.75rem', padding: '0 0.25rem', borderRadius: 2 }}>
              âˆ’{discount}%
            </span>
          )}
        </div>
        {/* Product name */}
        <h5 style={{ margin: '0.5rem 0', fontSize: '0.95rem', flex: '1 1 auto' }}>{product.name}</h5>
        {/* Rating and add to cart button */}
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
          <div style={{ fontSize: '0.875rem', color: 'var(--color-primary)' }}>
            {'â˜…'.repeat(Math.round(product.rating))}
            {'â˜†'.repeat(5 - Math.round(product.rating))}
            <span style={{ marginLeft: '0.25rem', color: 'var(--color-muted)' }}>{product.rating.toFixed(1)}</span>
          </div>
          <AddToCartButton product={product} />
        </div>
      </div>
    </div>
  );
}

/**
 * Separate component used to hook into the CartContext.  Splitting
 * into its own function avoids recreating the context reference on
 * each card render and keeps the JSX in ProductCard cleaner.
 */
function AddToCartButton({ product }) {
  const { addItem } = useContext(CartContext);
  return (
    <button
      className="add-to-cart-btn"
      onClick={(e) => {
        e.preventDefault();
        addItem(product);
      }}
      style={{ background: 'var(--color-primary)', border: 'none', color: '#fff', padding: '0.25rem 0.5rem', borderRadius: 4, cursor: 'pointer' }}
    >
      ðŸ›’
    </button>
  );
}

export default ProductCard;
