import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { categories } from '../data/categories';

/**
 * The Header component renders the top navigation bar along with a mobile
 * menu and search panel.  It is responsive: on larger screens the
 * categories live in a side menu triggered by the hamburger button.  On
 * small screens the search bar is hidden behind a search icon.  The
 * icons are represented using Unicode glyphs to avoid pulling in
 * external icon libraries.
 */
function Header() {
  // Track whether the search bar is expanded.  The supermenu is now always
  // visible and we no longer display a hamburger drawer on small screens.
  const [isSearchOpen, setSearchOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  // Track which top‚Äëlevel category is currently hovered to show a dropdown.
  // At the moment we don't have sub‚Äëcategories defined in our data but
  // this state allows future enhancements.  For now it simply highlights
  // the active link.
  const [activeCategory, setActiveCategory] = useState(null);

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim()) {
      // Redirect to a search results page.  For simplicity we reuse the
      // category page path and pass the term as a query string.
      navigate(`/category/search?query=${encodeURIComponent(searchTerm.trim())}`);
    }
    setSearchTerm('');
    setSearchOpen(false);
  };

  return (
    <header className="fixed top-0 left-0 w-full z-50 bg-white border-b border-gray-200">
      <div className="container px-4 h-16 flex items-center justify-between">
        {/* Left spacer to help center the logo */}
        <div className="w-1/4"></div>
        {/* Logo / shop name */}
        <div className="flex-1 text-center">
          <Link to="/" className="font-bold text-xl md:text-2xl text-accent">
            –ü–æ—Å—Ç–µ–ª—å–Ω–æ–µ¬†–ë–µ–ª—å–µ‚Äë–Æ–≥
          </Link>
        </div>
        {/* Icons */}
        <div className="w-1/4 flex justify-end items-center gap-4 text-xl">
          <button
            onClick={() => setSearchOpen(!isSearchOpen)}
            aria-label="–ü–æ–∏—Å–∫"
            className="focus:outline-none transition-transform transform hover:scale-110 active:scale-90"
          >
            üîç
          </button>
          <Link
            to="/cart"
            aria-label="–ö–æ—Ä–∑–∏–Ω–∞"
            className="transition-transform transform hover:scale-110 active:scale-90"
          >
            üõí
          </Link>
          <Link
            to="/login"
            aria-label="–õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç"
            className="transition-transform transform hover:scale-110 active:scale-90"
          >
            üë§
          </Link>
        </div>
      </div>
      {/* Super menu: a horizontal list of categories centered.  On small screens it scrolls horizontally. */}
      <nav className="border-t border-gray-200 bg-white">
        <div className="container px-4 overflow-x-auto">
          <ul className="flex flex-nowrap items-center justify-center gap-6 text-sm">
            {categories.map((cat) => (
              <li
                key={cat.slug}
                className="relative py-2"
                onMouseEnter={() => setActiveCategory(cat.slug)}
                onMouseLeave={() => setActiveCategory(null)}
              >
                <Link
                  to={`/category/${cat.slug}`}
                  className={`hover:text-primary transition-colors ${activeCategory === cat.slug ? 'text-primary' : ''}`}
                >
                  {cat.name}
                </Link>
                {/* Reserved for future dropdowns */}
              </li>
            ))}
          </ul>
        </div>
      </nav>
      {/* Search bar */}
      {isSearchOpen && (
        <form onSubmit={handleSearchSubmit} className="border-t border-gray-200 bg-white">
          <div className="container px-4 py-2 flex gap-2">
            <input
              type="text"
              placeholder="–ü–æ–∏—Å–∫..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="flex-1 px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring focus:border-primary"
            />
            <button type="submit" className="button whitespace-nowrap">
              –ò—Å–∫–∞—Ç—å
            </button>
          </div>
        </form>
      )}
    </header>
  );
}

export default Header;
