import React from 'react';
import { Link } from 'react-router-dom';

/**
 * The Footer component contains grouped lists of informational links,
 * contact details and a simple newsletter subscription form.  In the
 * original site the footer also displays social network and payment
 * logos; here we replicate their presence using placeholder text.  At
 * the very bottom we render the copyright notice.  For any link that
 * doesn’t yet exist in this replica we still provide a clickable
 * placeholder that navigates nowhere.
 */
function Footer() {
  const currentYear = new Date().getFullYear();
  const groups = [
    {
      title: 'Все о Cozy Home',
      links: [
        { label: 'О компании', path: '/about' },
        { label: 'Блог', path: '/blog' },
        { label: 'Акции и новости', path: '/news' },
      ],
    },
    {
      title: 'Покупателям',
      links: [
        { label: 'Доставка и самовывоз', path: '/shipping' },
        { label: 'Пункты выдачи заказов', path: '/pickup' },
        { label: 'Способы оплаты', path: '/payment' },
        { label: 'Возврат товара', path: '/returns' },
        { label: 'Таблица размеров', path: '/sizes' },
        { label: 'Подарочные сертификаты', path: '/giftcards' },
        { label: 'Программа лояльности', path: '/loyalty' },
      ],
    },
    {
      title: 'Свяжитесь с нами',
      links: [
        { label: 'Контакты', path: '/contact' },
        { label: 'Адреса магазинов', path: '/stores' },
        { label: 'Поставщикам', path: '/suppliers' },
      ],
    },
  ];

  return (
    <footer style={{ backgroundColor: '#fff', borderTop: '1px solid var(--color-border)', padding: '2rem 0' }}>
      <div className="container" style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem' }}>
        {/* Discount banner for the mobile app */}
        <div style={{ flex: '1 1 300px', minWidth: '260px' }}>
          <h3 style={{ marginTop: 0 }}>Скачайте наше мобильное приложение</h3>
          <p>Скидка 1500 ₽ на первую покупку при заказе от 6000 ₽.</p>
          <div style={{ display: 'flex', gap: '1rem' }}>
            <a href="#" className="button" style={{ backgroundColor: '#000' }}>
              App Store
            </a>
            <a href="#" className="button" style={{ backgroundColor: '#000' }}>
              Google Play
            </a>
          </div>
        </div>
        {/* Link groups */}
        {groups.map((grp) => (
          <div key={grp.title} style={{ flex: '1 1 150px', minWidth: '150px' }}>
            <h4 style={{ marginTop: 0 }}>{grp.title}</h4>
            <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
              {grp.links.map((link) => (
                <li key={link.label} style={{ marginBottom: '0.5rem' }}>
                  {/* Use Link for internal navigation when possible */}
                  {link.path.startsWith('/') ? (
                    <Link to={link.path}>{link.label}</Link>
                  ) : (
                    <a href="#">{link.label}</a>
                  )}
                </li>
              ))}
            </ul>
          </div>
        ))}
        {/* Newsletter subscription */}
        <div style={{ flex: '1 1 300px', minWidth: '260px' }}>
          <h4 style={{ marginTop: 0 }}>Подпишитесь на новости COZY HOME</h4>
          <p>Будьте в курсе новинок и специальных предложений.</p>
          <form onSubmit={(e) => e.preventDefault()} style={{ display: 'flex', gap: '0.5rem' }}>
            <input
              type="email"
              placeholder="Ваш e‑mail"
              required
              style={{ flex: 1, padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
            />
            <button type="submit" className="button" style={{ whiteSpace: 'nowrap' }}>
              Отправить
            </button>
          </form>
        </div>
      </div>
      {/* Bottom bar */}
      <div style={{ borderTop: '1px solid var(--color-border)', marginTop: '2rem', paddingTop: '1rem' }}>
        <div className="container" style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'space-between', alignItems: 'center' }}>
          <p style={{ margin: 0 }}>© Cozy Home, 2015–{currentYear}</p>
          <p style={{ margin: 0, fontSize: '0.875rem', color: 'var(--color-muted)' }}>
            Данный сайт является учебной копией и не предназначен для коммерческого использования.
          </p>
        </div>
      </div>
    </footer>
  );
}

export default Footer;
