import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { categories } from '../data/categories';

/**
 * The Header component renders the top navigation bar along with a mobile
 * menu and search panel.  It is responsive: on larger screens the
 * categories live in a side menu triggered by the hamburger button.  On
 * small screens the search bar is hidden behind a search icon.  The
 * icons are represented using Unicode glyphs to avoid pulling in
 * external icon libraries.
 */
function Header() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [isSearchOpen, setSearchOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim()) {
      // Redirect to a search results page.  For simplicity we reuse the
      // category page path and pass the term as a query string.
      navigate(`/category/search?query=${encodeURIComponent(searchTerm.trim())}`);
    }
    setSearchTerm('');
    setSearchOpen(false);
  };

  return (
    <header className="site-header" style={{ position: 'fixed', top: 0, width: '100%', zIndex: 1000, backgroundColor: '#fff', borderBottom: '1px solid var(--color-border)' }}>
      <div className="header-inner container" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', height: 'var(--header-height)' }}>
        {/* Hamburger button to toggle mobile menu */}
        <button
          className="menu-btn"
          style={{ fontSize: '1.5rem', background: 'none', border: 'none', cursor: 'pointer' }}
          onClick={() => setMenuOpen(!isMenuOpen)}
        >
          ‚ò∞
        </button>
        {/* Logo */}
        <Link to="/" className="logo" style={{ fontWeight: 700, fontSize: '1.25rem', color: 'var(--color-accent)' }}>
          COZY HOME
        </Link>
        {/* Icon group */}
        <div className="icon-group" style={{ display: 'flex', gap: '1rem', alignItems: 'center' }}>
          <button
            onClick={() => setSearchOpen(!isSearchOpen)}
            style={{ background: 'none', border: 'none', cursor: 'pointer', fontSize: '1.25rem' }}
            aria-label="–ü–æ–∏—Å–∫"
          >
            üîç
          </button>
          <Link to="/cart" aria-label="–ö–æ—Ä–∑–∏–Ω–∞" style={{ fontSize: '1.25rem' }}>
            üõí
          </Link>
          <Link to="/login" aria-label="–õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç" style={{ fontSize: '1.25rem' }}>
            üë§
          </Link>
        </div>
      </div>
      {/* Search bar overlay */}
      {isSearchOpen && (
        <form onSubmit={handleSearchSubmit} className="search-bar" style={{ borderTop: '1px solid var(--color-border)', backgroundColor: '#fff' }}>
          <div className="container" style={{ padding: '0.5rem 1rem', display: 'flex', gap: '0.5rem' }}>
            <input
              type="text"
              placeholder="–ü–æ–∏—Å–∫..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              style={{ flex: 1, padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
            />
            <button type="submit" className="button" style={{ whiteSpace: 'nowrap' }}>
              –ò—Å–∫–∞—Ç—å
            </button>
          </div>
        </form>
      )}
      {/* Side menu overlay */}
      {isMenuOpen && (
        <div className="side-menu-wrapper" style={{ position: 'fixed', top: 0, left: 0, width: '100%', height: '100%', display: 'flex', zIndex: 999 }}>
          {/* Clickable backdrop to close menu */}
          <div
            className="backdrop"
            style={{ flex: 1, backgroundColor: 'rgba(0,0,0,0.3)' }}
            onClick={() => setMenuOpen(false)}
          ></div>
          {/* Menu content */}
          <nav
            className="side-menu-content"
            style={{ width: '280px', maxWidth: '80%', backgroundColor: '#fff', boxShadow: '0 0 10px rgba(0,0,0,0.1)', padding: '1rem', overflowY: 'auto' }}
          >
            <h2 style={{ marginTop: 0 }}>–ö–∞—Ç–∞–ª–æ–≥</h2>
            <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
              {categories.map((cat) => (
                <li key={cat.slug} style={{ marginBottom: '0.5rem' }}>
                  <Link
                    to={`/category/${cat.slug}`}
                    onClick={() => setMenuOpen(false)}
                    style={{ color: 'var(--color-text)' }}
                  >
                    {cat.name}
                  </Link>
                </li>
              ))}
            </ul>
            <hr style={{ margin: '1rem 0' }} />
            <ul style={{ listStyle: 'none', padding: 0, margin: 0 }}>
              <li style={{ marginBottom: '0.5rem' }}>
                <Link to="/about" onClick={() => setMenuOpen(false)}>–û –∫–æ–º–ø–∞–Ω–∏–∏</Link>
              </li>
              <li style={{ marginBottom: '0.5rem' }}>
                <Link to="/cart" onClick={() => setMenuOpen(false)}>–ö–æ—Ä–∑–∏–Ω–∞</Link>
              </li>
              <li>
                <Link to="/login" onClick={() => setMenuOpen(false)}>–í—Ö–æ–¥ / –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è</Link>
              </li>
            </ul>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
