import React, { useState } from 'react';

/**
 * LoginPage offers two simple ways for a visitor to authenticate:
 * via a one‑time SMS code or using a password.  No real
 * authentication is performed; submitting either form will display
 * a confirmation alert.  This page roughly corresponds to the
 * login/registration modal on the original site.
 */
function LoginPage() {
  const [tab, setTab] = useState('sms');
  const [phone, setPhone] = useState('');
  const [code, setCode] = useState('');
  const [password, setPassword] = useState('');

  const handleSmsSubmit = (e) => {
    e.preventDefault();
    alert('Код отправлен на номер ' + phone);
  };
  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    alert('Вход выполнен');
  };

  return (
    <div className="login-page" style={{ padding: '2rem 0' }}>
      <div className="container" style={{ maxWidth: '400px' }}>
        <h1>Вход или регистрация</h1>
        <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem' }}>
          <button
            onClick={() => setTab('sms')}
            style={{ flex: 1, padding: '0.5rem', borderBottom: tab === 'sms' ? '2px solid var(--color-primary)' : '1px solid var(--color-border)', background: 'none', borderTop: 'none', borderLeft: 'none', borderRight: 'none', cursor: 'pointer' }}
          >
            Через SMS
          </button>
          <button
            onClick={() => setTab('password')}
            style={{ flex: 1, padding: '0.5rem', borderBottom: tab === 'password' ? '2px solid var(--color-primary)' : '1px solid var(--color-border)', background: 'none', borderTop: 'none', borderLeft: 'none', borderRight: 'none', cursor: 'pointer' }}
          >
            По паролю
          </button>
        </div>
        {tab === 'sms' && (
          <form onSubmit={handleSmsSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <input
              type="tel"
              placeholder="Телефон"
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
              style={{ padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
            />
            <button type="submit" className="button">Получить код</button>
            {code && (
              <input
                type="text"
                placeholder="Введите код"
                value={code}
                onChange={(e) => setCode(e.target.value)}
                style={{ padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
              />
            )}
          </form>
        )}
        {tab === 'password' && (
          <form onSubmit={handlePasswordSubmit} style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
            <input
              type="text"
              placeholder="Телефон или e‑mail"
              required
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              style={{ padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
            />
            <input
              type="password"
              placeholder="Пароль"
              required
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              style={{ padding: '0.5rem', border: '1px solid var(--color-border)', borderRadius: 4 }}
            />
            <button type="submit" className="button">Войти</button>
          </form>
        )}
      </div>
    </div>
  );
}

export default LoginPage;
