import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { categories } from '../data/categories';

/**
 * The Header component renders the top navigation bar along with a mobile
 * menu and search panel.  It is responsive: on larger screens the
 * categories live in a side menu triggered by the hamburger button.  On
 * small screens the search bar is hidden behind a search icon.  The
 * icons are represented using Unicode glyphs to avoid pulling in
 * external icon libraries.
 */
function Header() {
  const [isMenuOpen, setMenuOpen] = useState(false);
  const [isSearchOpen, setSearchOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState('');
  const navigate = useNavigate();

  const handleSearchSubmit = (event) => {
    event.preventDefault();
    if (searchTerm.trim()) {
      // Redirect to a search results page.  For simplicity we reuse the
      // category page path and pass the term as a query string.
      navigate(`/category/search?query=${encodeURIComponent(searchTerm.trim())}`);
    }
    setSearchTerm('');
    setSearchOpen(false);
  };

  return (
    <header className="fixed top-0 left-0 w-full z-50 bg-white border-b border-gray-200">
      <div className="container flex items-center justify-between h-16 px-4">
        {/* Hamburger button */}
        <button
          className="text-2xl focus:outline-none"
          onClick={() => setMenuOpen(!isMenuOpen)}
        >
          ‚ò∞
        </button>
        {/* Logo */}
        <Link to="/" className="font-bold text-xl text-accent">
          COZY HOME
        </Link>
        {/* Icons */}
        <div className="flex items-center gap-4 text-xl">
          <button
            onClick={() => setSearchOpen(!isSearchOpen)}
            aria-label="–ü–æ–∏—Å–∫"
            className="focus:outline-none"
          >
            üîç
          </button>
          <Link to="/cart" aria-label="–ö–æ—Ä–∑–∏–Ω–∞">
            üõí
          </Link>
          <Link to="/login" aria-label="–õ–∏—á–Ω—ã–π –∫–∞–±–∏–Ω–µ—Ç">
            üë§
          </Link>
        </div>
      </div>
      {/* Search bar */}
      {isSearchOpen && (
        <form onSubmit={handleSearchSubmit} className="border-t border-gray-200 bg-white">
          <div className="container px-4 py-2 flex gap-2">
            <input
              type="text"
              placeholder="–ü–æ–∏—Å–∫..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="flex-1 px-3 py-2 border border-gray-300 rounded focus:outline-none focus:ring focus:border-primary"
            />
            <button type="submit" className="button whitespace-nowrap">
              –ò—Å–∫–∞—Ç—å
            </button>
          </div>
        </form>
      )}
      {/* Side menu overlay */}
      {isMenuOpen && (
        <div className="fixed inset-0 z-40 flex">
          {/* Backdrop */}
          <div
            className="flex-1 bg-black/30"
            onClick={() => setMenuOpen(false)}
          ></div>
          {/* Menu */}
          <nav className="w-72 max-w-[80%] bg-white shadow-lg p-4 overflow-y-auto">
            <h2 className="text-lg font-semibold mb-4">–ö–∞—Ç–∞–ª–æ–≥</h2>
            <ul className="space-y-2">
              {categories.map((cat) => (
                <li key={cat.slug}>
                  <Link
                    to={`/category/${cat.slug}`}
                    onClick={() => setMenuOpen(false)}
                    className="block text-gray-700 hover:text-primary"
                  >
                    {cat.name}
                  </Link>
                </li>
              ))}
            </ul>
            <hr className="my-4" />
            <ul className="space-y-2">
              <li>
                <Link to="/about" onClick={() => setMenuOpen(false)} className="block hover:text-primary">
                  –û –∫–æ–º–ø–∞–Ω–∏–∏
                </Link>
              </li>
              <li>
                <Link to="/cart" onClick={() => setMenuOpen(false)} className="block hover:text-primary">
                  –ö–æ—Ä–∑–∏–Ω–∞
                </Link>
              </li>
              <li>
                <Link to="/login" onClick={() => setMenuOpen(false)} className="block hover:text-primary">
                  –í—Ö–æ–¥ / –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è
                </Link>
              </li>
            </ul>
          </nav>
        </div>
      )}
    </header>
  );
}

export default Header;
