import React, { useState, useContext } from 'react';
import { useParams } from 'react-router-dom';
import { products } from '../data/products';
import { reviews } from '../data/reviews';
import { CartContext } from '../contexts/CartContext';

/**
 * ProductPage renders detailed information about a single product.  It
 * includes a gallery with thumbnails (using a single placeholder here),
 * pricing information, a description and a simple tabbed interface
 * replicating the various informational panels from the original site.
 */
function ProductPage() {
  const { id } = useParams();
  const product = products.find((p) => p.id === id);
  const productReviews = reviews.filter((r) => r.productId === id);
  const [activeTab, setActiveTab] = useState('about');

  const { addItem } = useContext(CartContext);

  if (!product) {
    return (
      <div className="container" style={{ padding: '2rem 0' }}>
        <h1>Товар не найден</h1>
        <p>К сожалению, продукта с указанным идентификатором не существует.</p>
      </div>
    );
  }

  return (
    <div className="product-page" style={{ padding: '2rem 0' }}>
      <div className="container" style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem' }}>
        {/* Image gallery */}
        <div style={{ flex: '1 1 300px', minWidth: '280px' }}>
          <div style={{ position: 'relative', paddingTop: '75%', backgroundColor: '#e9e7e3', borderRadius: 4 }}></div>
          <div style={{ display: 'flex', gap: '0.5rem', marginTop: '0.5rem' }}>
            {Array.from({ length: 4 }).map((_, idx) => (
              <div
                key={idx}
                style={{ flex: 1, paddingTop: '75%', backgroundColor: '#f3f1ef', borderRadius: 4 }}
              ></div>
            ))}
          </div>
        </div>
        {/* Product information */}
        <div style={{ flex: '1 1 300px', minWidth: '280px' }}>
          <h1>{product.name}</h1>
          <div style={{ marginBottom: '0.5rem', color: 'var(--color-primary)', fontSize: '1.5rem', fontWeight: 600 }}>
            {product.price.toLocaleString('ru-RU')} ₽
            {product.oldPrice && (
              <span style={{ fontSize: '1rem', textDecoration: 'line-through', color: 'var(--color-muted)', marginLeft: '0.5rem' }}>
                {product.oldPrice.toLocaleString('ru-RU')} ₽
              </span>
            )}
          </div>
          <div style={{ color: 'var(--color-primary)', fontSize: '1rem', marginBottom: '0.5rem' }}>
            {'★'.repeat(Math.round(product.rating))}{'☆'.repeat(5 - Math.round(product.rating))}
            <span style={{ marginLeft: '0.25rem', color: 'var(--color-muted)' }}>{product.rating.toFixed(1)}</span>
          </div>
          {/* Add to cart button */}
          <button
            className="button"
            onClick={() => addItem(product)}
            style={{ marginBottom: '1rem' }}
          >
            Добавить в корзину
          </button>
          {/* Tabs */}
          <div style={{ borderBottom: '1px solid var(--color-border)', marginBottom: '1rem', display: 'flex', gap: '1rem' }}>
            <button
              onClick={() => setActiveTab('about')}
              style={{ background: 'none', border: 'none', padding: '0.5rem 0', cursor: 'pointer', borderBottom: activeTab === 'about' ? '2px solid var(--color-primary)' : 'none' }}
            >
              О товаре
            </button>
            <button
              onClick={() => setActiveTab('reviews')}
              style={{ background: 'none', border: 'none', padding: '0.5rem 0', cursor: 'pointer', borderBottom: activeTab === 'reviews' ? '2px solid var(--color-primary)' : 'none' }}
            >
              Отзывы ({productReviews.length})
            </button>
            <button
              onClick={() => setActiveTab('details')}
              style={{ background: 'none', border: 'none', padding: '0.5rem 0', cursor: 'pointer', borderBottom: activeTab === 'details' ? '2px solid var(--color-primary)' : 'none' }}
            >
              Характеристики
            </button>
          </div>
          {/* Tab content */}
          {activeTab === 'about' && (
            <div>
              <p style={{ marginTop: 0 }}>{product.description}</p>
            </div>
          )}
          {activeTab === 'reviews' && (
            <div>
              {productReviews.length > 0 ? (
                productReviews.map((rev, idx) => (
                  <div key={idx} style={{ marginBottom: '1rem', borderBottom: '1px solid var(--color-border)', paddingBottom: '0.5rem' }}>
                    <div style={{ color: 'var(--color-primary)', fontSize: '0.9rem' }}>
                      {'★'.repeat(rev.rating)}{'☆'.repeat(5 - rev.rating)}
                    </div>
                    <p style={{ margin: '0.25rem 0' }}>{rev.text}</p>
                    <p style={{ margin: 0, fontSize: '0.85rem', color: 'var(--color-muted)', fontStyle: 'italic' }}>— {rev.author}</p>
                  </div>
                ))
              ) : (
                <p>На этот товар пока нет отзывов.</p>
              )}
            </div>
          )}
          {activeTab === 'details' && (
            <div>
              <p>Здесь будут приведены подробные характеристики товара. Добавьте необходимые поля в объект продукта.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default ProductPage;
