import React from 'react';

/**
 * AdminContent manages static pages and blog posts.  This placeholder
 * outlines the intended capabilities including page editing, blog
 * management, menu configuration and banner updates.
 */
function AdminContent() {
  return (
    <div>
      <h1 className="text-2xl font-semibold mb-4">Управление контентом</h1>
      <p className="text-gray-600">В этой секции вы сможете создавать и редактировать статические страницы (О нас, Контакты, FAQ), вести блог, управлять меню и баннерами на главной странице.  Реализация rich‑text редактора и загрузки изображений будет добавлена при подключении к бэкенду.</p>
    </div>
  );
}

export default AdminContent;
