import React, { useContext } from 'react';
import { CartContext } from '../contexts/CartContext';
import { Link } from 'react-router-dom';

/**
 * CartPage displays the contents of the shopping cart.  Users can
 * increment, decrement or remove items, and view an order summary.
 * Since this is a front‑end only application there is no actual
 * checkout process; the “Оформить заказ” button serves as a
 * placeholder for future integration.
 */
function CartPage() {
  const { items, addItem, removeItem, updateQuantity, clearCart } = useContext(CartContext);

  const total = items.reduce((sum, item) => sum + item.product.price * item.quantity, 0);

  return (
    <div className="cart-page" style={{ padding: '2rem 0' }}>
      <div className="container">
        <h1>Корзина</h1>
        {items.length === 0 ? (
          <p>Ваша корзина пуста. Перейдите в <Link to="/">каталог</Link>, чтобы добавить товары.</p>
        ) : (
          <div style={{ display: 'flex', flexWrap: 'wrap', gap: '2rem' }}>
            {/* Item list */}
            <div style={{ flex: '2 1 400px', minWidth: '280px' }}>
              {items.map((item) => (
                <div
                  key={item.product.id}
                  style={{ display: 'flex', alignItems: 'center', gap: '1rem', borderBottom: '1px solid var(--color-border)', padding: '1rem 0' }}
                >
                  <div
                    style={{ width: '80px', height: '80px', backgroundColor: '#e9e7e3', borderRadius: 4 }}
                  ></div>
                  <div style={{ flex: 1 }}>
                    <h4 style={{ margin: 0 }}>{item.product.name}</h4>
                    <div style={{ color: 'var(--color-primary)', fontWeight: 600 }}>
                      {item.product.price.toLocaleString('ru-RU')} ₽
                    </div>
                    <div style={{ display: 'flex', alignItems: 'center', marginTop: '0.25rem' }}>
                      <button
                        onClick={() => updateQuantity(item.product.id, item.quantity - 1)}
                        style={{ background: 'none', border: '1px solid var(--color-border)', padding: '0 0.5rem', borderRadius: 4, cursor: 'pointer' }}
                      >
                        −
                      </button>
                      <span style={{ margin: '0 0.5rem' }}>{item.quantity}</span>
                      <button
                        onClick={() => updateQuantity(item.product.id, item.quantity + 1)}
                        style={{ background: 'none', border: '1px solid var(--color-border)', padding: '0 0.5rem', borderRadius: 4, cursor: 'pointer' }}
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-end', gap: '0.5rem' }}>
                    <span style={{ fontWeight: 600 }}>
                      {(item.product.price * item.quantity).toLocaleString('ru-RU')} ₽
                    </span>
                    <button
                      onClick={() => removeItem(item.product.id)}
                      style={{ background: 'none', border: 'none', color: 'var(--color-muted)', cursor: 'pointer', fontSize: '0.875rem' }}
                    >
                      Удалить
                    </button>
                  </div>
                </div>
              ))}
            </div>
            {/* Summary */}
            <div style={{ flex: '1 1 250px', minWidth: '250px', border: '1px solid var(--color-border)', borderRadius: 4, padding: '1rem', backgroundColor: '#fff' }}>
              <h3>Сводка заказа</h3>
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Товары ({items.length})</span>
                <span>{total.toLocaleString('ru-RU')} ₽</span>
              </div>
              {/* Placeholder for shipping calculation */}
              <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: '0.5rem' }}>
                <span>Доставка</span>
                <span>0 ₽</span>
              </div>
              <hr />
              <div style={{ display: 'flex', justifyContent: 'space-between', margin: '0.5rem 0 1rem', fontWeight: 600 }}>
                <span>Итого</span>
                <span>{total.toLocaleString('ru-RU')} ₽</span>
              </div>
              <button className="button" style={{ width: '100%', marginBottom: '0.5rem' }} onClick={() => alert('Завершение заказа не реализовано')}>Оформить заказ</button>
              <button
                className="button"
                style={{ width: '100%', backgroundColor: 'var(--color-muted)', color: '#fff' }}
                onClick={clearCart}
              >
                Очистить корзину
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default CartPage;
